import gmpy2

e_new = gmpy2.mpz('0x10001')
n1 = gmpy2.mpz(
    '')
c1 = gmpy2.mpz(
    '')
c2 = gmpy2.mpz(
    '')
# (n1 * E) + n2 = n3
mixed = gmpy2.mpz(
    '')

n2 = gmpy2.mpz(mixed % n1)


def computeGCD(x, y):
    while (y):
        x, y = y, x % y
    return abs(x)


q_calculated = gmpy2.mpz(computeGCD(n1, n2))
p_calculated = gmpy2.c_div(n1, q_calculated)
z_calculated = gmpy2.c_div(n2, q_calculated)

d1 = gmpy2.invert(e_new, (p_calculated - 1) * (q_calculated - 1))
d2 = gmpy2.invert(e_new, (q_calculated - 1) * (z_calculated - 1))

p1 = pow(c1, d1, n1)
p2 = pow(c2, d2, n2)

p12 = gmpy2.to_binary(p1)
p22 = gmpy2.to_binary(p2)

d1 = p12.decode('utf-8')
d2 = p22.decode('utf-8')

print(d1)
print(d2)

print(d1[::-1] + d2[::-1])
